interface FindUsCardProps {
  title: string;
  logoLight: ReactNode;
  logoDark: ReactNode;
}

const FindUsCard = (props: FindUsCardProps) => {
  const { title, logoLight, logoDark } = props;
  const DiscordCardWrapper = styled.div`
    position: relative;
    .shadow {
      transition: 0.6s;
      position: absolute;
      transform: translate(-6px, 6px);
    }
    .card {
      z-index: 3;
    }
    .content {
      position: absolute;
      z-index: 4;
      .card-logo {
        position: absolute;
        top: -38px;
        left: -70px;
      }
      .card-title {
        position: absolute;
        top: -33.5px;
        left: -12px;
        font-family: 'Montserrat', serif;
        font-style: normal;
        font-weight: 700;
        font-size: 18px;
        line-height: 150%;
        color: #4e3188;
      }
      .card-link {
        position: absolute;
        top: 22px;
        left: -65px;
        font-family: 'Montserrat', serif;
        font-style: normal;
        font-weight: 600;
        font-size: 16px;
        line-height: 150%;
        text-align: center;
        color: #4e3188;
        > div {
          transition: 0.6s;
        }
        > div:first-child {
          white-space: nowrap;
          width: 110px;
        }
        > div:last-child {
          margin-left: 4px;
        }
        .fu-dark {
          margin-left: -14px;
        }
      }
    }
    .fu-light {
      visibility: hidden;
    }
    .fu-dark {
      visibility: visible;
    }
    :hover {
      .shadow {
        transform: translate(-3px, 3px);
      }
      .card {
        > svg > path {
          fill: #4e3188;
        }
      }
      .content {
      }
      .card-title,
      .card-link > div {
        background: linear-gradient(
          150.38deg,
          #54b8ec -0.32%,
          #54b8ec 7.09%,
          #acd776 29.79%,
          #bbdd61 33.69%,
          #c8e150 37.03%,
          #d1e544 39.26%,
          #e0ea30 43.23%,
          #e4eb2a 44.53%,
          #eeef1d 46.52%,
          #f7f211 49.44%,
          #f7f53e 54.31%,
          #f7f53e 54.33%,
          #f7f53e 57.1%,
          #f7f65a 61%,
          #ebf366 65.46%,
          #e4f16d 68.25%,
          #d9ee78 72.71%,
          #83d4ce 106.72%
        );
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
        text-fill-color: transparent;
      }
      .fu-light {
        visibility: visible;
      }
      .fu-dark {
        visibility: hidden;
      }
      .card-link {
        > div:first-child {
          //transform: translate(20px, 0);
          letter-spacing: 0.1em;
        }
        > div:last-child {
          //transform: translate(20px, -8px);
          transform: translate(8px, -4px);
        }
      }
    }
  `;

  return (
    <DiscordCardWrapper>
      <div className="shadow">
        <Shadow />
      </div>
      <div className="card">
        <Card />
      </div>
      <div className="content">
        <div className="card-logo fu-light">{logoLight}</div>
        <div className="card-logo fu-dark">{logoDark}</div>
        <div className="card-title">{title}</div>
        <div className="card-link">
          <div>Learn More</div>
          <div>
            <div className="fu-light">
              <ArrowOutwardColorful />
            </div>
            <div className="fu-dark">
              <ArrowOutwardDark />
            </div>
          </div>
        </div>
      </div>
    </DiscordCardWrapper>
  );
};

export const DiscordCard = () => (
  <FindUsCard title="Discord" logoLight={<DiscordColorful />} logoDark={<DiscordDark />} />
);

export const GithubCard = () => <FindUsCard title="Github" logoLight={<GithubColorful />} logoDark={<GithubDark />} />;

export const MediumCard = () => <FindUsCard title="Medium" logoLight={<MediumColorful />} logoDark={<MediumDark />} />;

export const TwitterCard = () => (
  <FindUsCard title="Twitter" logoLight={<TwitterColorful />} logoDark={<TwitterDark />} />
);
